import React, { useState } from 'react';
import axios from 'axios';

function App() 
{
  const [query, setQuery] = useState('');
  const [food, setFood] = useState(
    { calories: '',
    carbohydrates_total_g: '',
    cholesterol_mg: '',
    fat_saturated_g: '',
    fat_total_g: '',
    fiber_g: '',
    name: '',
    potassium_mg: '',
    protein_g: '',
    serving_size_g: '',
    sodium_mg: '',
    sugar_g: '', }
    );
  const [error, setError] = useState(''); 

  var options = {
    method: 'GET',
    url: 'https://calorieninjas.p.rapidapi.com/v1/nutrition',
    params: {query: query},
    headers: {
      'x-rapidapi-host': 'calorieninjas.p.rapidapi.com',
      'x-rapidapi-key': '2b42e0b829msh548ad463fe29e98p185282jsnad0888fb5232'
    }
  };
  const search=(e) =>
  {
    if (e.key === "Enter") {
      axios.request(options).then(function (response) 
      {
        const data= (response.data.items);
        if(data.length)
        {
         const d= data[0];
         console.log(d.calories);
         setFood(
          { 
          ...food,
          calories: d.calories,
          carbohydrates_total_g: '',
          cholesterol_mg: '',
          fat_saturated_g: '',
          fat_total_g: '',
          fiber_g: '',
          name: '',
          potassium_mg: '',
          protein_g: '',
          serving_size_g: '',
          sodium_mg: '',
          sugar_g: '', }
         );

         setError(null);
         console.log(food);
        }
        // else
        // {
        //   setError("Food item not found");
        //   setFood(null);
        //   console.log(error);          
        // }
      }
      )
      .catch(function (error) {
        setFood(null);
        console.error(error);
      });
    }
  }
  return (
    <div >
      <main>
        <div className="search-box">
          <input 
          type="text" placeholder="Enter Food Item"
          onChange= {(e)=> setQuery(e.target.value)}
          value= {query}
          onKeyPress= {search}
          />
        </div>
      </main>
    </div>
  );
}

export default App;
