import React, { useState } from 'react';
import axios from 'axios';

function App() 
{
  const [query, setQuery] = useState('');
  const [food, setFood] = useState( null
    // { calories: '',
    // carbohydrates_total_g: '',
    // cholesterol_mg: '',
    // fat_saturated_g: '',
    // fat_total_g: '',
    // fiber_g: '',
    // name: '',
    // potassium_mg: '',
    // protein_g: '',
    // serving_size_g: '',
    // sodium_mg: '',
    // sugar_g: '', }
    );
  const [error, setError] = useState(''); 

  var options = {
    method: 'GET',
    url: 'https://calorieninjas.p.rapidapi.com/v1/nutrition',
    params: {query: query},
    headers: {
      'x-rapidapi-host': 'calorieninjas.p.rapidapi.com',
      'x-rapidapi-key': '2b42e0b829msh548ad463fe29e98p185282jsnad0888fb5232'
    }
  };
  const search=async(e) =>
  {
    if (e.key === "Enter") {
      await axios.request(options)
      .then((response) => 
      {
        const data= (response.data.items);
        if(data.length)
        {
         const d= data[0];

         setFood(
          { 
          calories: d.calories,
          carbohydrates_total_g: d.carbohydrates_total_g,
          cholesterol_mg: d.cholesterol_mg,
          fat_saturated_g: d.fat_saturated_g,
          fat_total_g: d.fat_total_g,
          fiber_g: d.fiber_g,
          potassium_mg: d.potassium_mg,
          protein_g: d.protein_g,
          serving_size_g: d.serving_size_g,
          sodium_mg: d.sodium_mg,
          sugar_g: d.sugar_g, }
         );
         setError(null);
        }
        else
        {
          setError("Food item not found");
          setFood(null);      
        }
      }
      )
      .catch(function (error) {
        setFood(null);
        setError(error);
      });
    }         
  }
  return (
    <div >
      <main>
        <div className="search-box">
          <input 
          type="text" placeholder="Enter Food Item"
          onChange= {(e)=> setQuery(e.target.value)}
          value= {query}
          onKeyPress= {search}
          />
        </div>
        <div>
          {
            food && 
            Object.entries(food).forEach(([key, value]) => {
              return (
                <div> {key}</div>
              )
            })          
          } 
          </div>

        <div>{error && error} </div>
      </main>
    </div>
  );
}

export default App;
